mapper.select:
select a.review_id, a.user_id_fk, a.festival_id_fk, a.review_nickname, a.review_content, to_char(a.review_date, 'yy-mm-dd') review_date1, a.review_image, a.review_readnum , b.festival_name, (select count(r_likes_id) from r_likes c where c.review_id_fk=a.review_id) likes from( select row_number() over(order by review_date desc) rn, reviews.* from reviews where festival_id_fk=#{userId} order by review_date desc) a, festivals b
where a.rn > #{start} and a.rn <= #{end}
and a.festival_id_fk=#{userId} and a.festival_id_fk=b.festival_id

        select c.* from(select rownum rn, b.* from (select a.* from festivals a

		order by a.festival_start desc) b)c 
        where rn > #{start} and rn <= #{end}
